var fbVisApprovals=new Class({Implements:[Options],options:{},initialize:function(b,a){this.setOptions(a);this.el=document.id(b);document.addEvent("click:relay(a.approve)",function(d){var c=d.target;d.stop();if(c.get("tag")!=="a"){c=c.findUp("a")}new Request.HTML({url:c.href,onSuccess:function(){c.getParent("tr").dispose()}}).send()});document.addEvent("click:relay(a.disapprove)",function(d){var c=d.target;d.stop();if(c.get("tag")!=="a"){c=c.findUp("a")}new Request.HTML({url:c.href,onSuccess:function(){c.getParent("tr").dispose()}}).send()});new FloatingTips(".approvalTip",{position:"right",content:function(d){var c=d.getNext();c.store("trigger",d);return c},hideOn:"mousedown"})}});